    pub fn attributeEquals(a: *const @import("attr").Attr, b: *const @import("attr").Attr) bool {

        // Check namespace
        if (a.namespace_uri == null and b.namespace_uri != null) return false;
        if (a.namespace_uri != null and b.namespace_uri == null) return false;
        if (a.namespace_uri != null and b.namespace_uri != null) {
