                    // Step 2: If prefix is "xmlns", return XMLNS namespace
                    if (std.mem.eql(u8, p, "xmlns")) {
                        return "http://www.w3.org/2000/xmlns/";
                    }
                }

                // Step 3: If namespace is non-null and prefix matches, return namespace
                if (elem.namespace_uri) |ns| {
